SELECT id, parent_id, menu_code, name, module, node_type, sort_order, url, created_at::timestamp as created_at
FROM menu
WHERE id = #{id}

SELECT id, parent_id, menu_code, name, module, node_type, sort_order, url, created_at::timestamp as created_at
FROM menu
WHERE menu_code = #{menuCode}

SELECT id, parent_id, menu_code, name, module, node_type, sort_order, url, created_at::timestamp as created_at
FROM menu


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND menu_code LIKE CONCAT('%', #{query.menuCode}, '%')


        AND module = #{query.module}


        AND node_type = #{query.nodeType}


        AND parent_id = #{query.parentId}


ORDER BY sort_order, id
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM menu


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND menu_code LIKE CONCAT('%', #{query.menuCode}, '%')


        AND module = #{query.module}


        AND node_type = #{query.nodeType}


        AND parent_id = #{query.parentId}
