mapper.select:
SELECT id, code, name, module, menu_code, action, created_at::timestamp as created_at
FROM permission
WHERE id = #{id}

mapper.update:
UPDATE permission
SET code = #{code}, name = #{name}, module = #{module}, menu_code = #{menuCode}, action = #{action}
WHERE id = #{id}

mapper.select:
SELECT id, code, name, module, menu_code, action, created_at::timestamp as created_at
FROM permission


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND code LIKE CONCAT('%', #{query.code}, '%')


        AND module = #{query.module}


        AND menu_code = #{query.menuCode}


        AND action = #{query.action}


ORDER BY module, menu_code, action, id
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM permission


        AND name LIKE CONCAT('%', #{query.name}, '%')


        AND code LIKE CONCAT('%', #{query.code}, '%')


        AND module = #{query.module}


        AND menu_code = #{query.menuCode}


        AND action = #{query.action}

SELECT id, code, name, module, menu_code, action, url, created_at::timestamp as created_at
FROM permission
